(147, 'theme_mods_onepress', 'a:26:{i:0;b:0;s:18:"custom_css_post_id";i:-1;s:21:"onepress_hero_disable";i:0;s:23:"onepress_hcl1_largetext";s:66:"We are <span class="js-rotating"> Donarity | with Humanity </span>";s:20:"onepress_hero_layout";s:1:"1";s:23:"onepress_hcl1_btn2_text";s:0:"";s:23:"onepress_hcl1_btn2_link";s:0:"";s:23:"onepress_hcl1_smalltext";s:318:"A donation is a gift given by physical or legal persons, typically for charitable purposes and/or to benefit a cause. A donation may take various forms, including cash offering, services, new or used goods,emergency, relief or humanitarian aid items, development aid support, and can also relate to medical care needs.";s:19:"onepress_hero_pdtop";s:2:"15";s:21:"onepress_hero_pdbotom";s:2:"15";s:20:"onepress_features_id";s:8:"features";s:23:"onepress_features_title";s:16:"Working Progress";s:26:"onepress_features_subtitle";s:23:"Our progress to success";s:23:"onepress_features_boxes";a:4:{i:0;a:6:{s:5:"title";s:7:"CLIMATE";s:9:"icon_type";s:5:"image";s:4:"icon";s:0:"";s:5:"image";a:2:{s:3:"url";s:69:"http://localhost/donarity/wp-content/uploads/2017/10/Climate_Icon.png";s:2:"id";i:16;}s:4:"desc";s:84:"<b>Climate</b>&nbsp;is the statistics of&nbsp;weather&nbsp;over long periods of time";s:4:"link";s:0:"";}i:1;a:6:{s:5:"title";s:8:"CHILDREN";s:9:"icon_type";s:5:"image";s:4:"icon";s:0:"";s:5:"image";a:2:{s:3:"url";s:67:"http://localhost/donarity/wp-content/uploads/2017/10/Untitled-1.png";s:2:"id";i:18;}s:4:"desc";s:78:"Child is a human being between the stages of&nbsp;birth&nbsp;and&nbsp;puberty.";s:4:"link";s:0:"";}i:2;a:6:{s:5:"title";s:11:"DEVELOPMENT";s:9:"icon_type";s:5:"image";s:4:"icon";s:0:"";s:5:"image";a:2:{s:3:"url";s:67:"http://localhost/donarity/wp-content/uploads/2017/10/Untitled-2.png";s:2:"id";i:17;}s:4:"desc";s:119:"Social&nbsp;<em>development</em>&nbsp;attempts to explain qualitative changes in the structure of&nbsp;<em>society</em>";s:4:"link";s:0:"";}i:3;a:6:{s:5:"title";s:8:"ALL TIME";s:9:"icon_